import { cart } from "./state";
import { NEW_CART } from "./type";

export const cartReducer = (state=cart, action)=>{
    switch(action.type){
        
        case NEW_CART:
        state.arr.push(action.value)
        break;
        default:
            break
    }

    
    return {...state}
}
