import { wishlist } from "./state";
import { WISHLIST} from "./type";

export const wishReducer = (state=wishlist, action)=>{
    switch(action.type){
        case WISHLIST://{}
            state.arr.push(action.value)
            
            break;
        
        default:
            break
    }

    
    return {...state}
}
