import { useForm } from "react-hook-form"


import React from 'react'

export const Signup = () => {
    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm();
    const onSubmit = (data) => {

    };
    return (
        <div>
            <form onSubmit={handleSubmit(onSubmit)}>
                <input {...register('name', {required:true})} />
                {errors.name && <p>name is required.</p>}
                <input {...register('surname', { required: true })} />
                {errors.surname && <p>surname is required.</p>}
                <input {...register('age', { pattern: /\d+/ , required:true})} />
                {errors.age && <p>Please enter number for age.</p>}
                <input {...register('email', { required: true , pattern:/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/})} />
                {errors.email && <p>email is required.</p>}
                <input {...register('password', { required: true})} />
                {errors.password && <p>password is required.</p>}
                <input {...register('confirm_password', { required: true })} />
                {errors.confirm_password && <p>confirm password is required.</p>}
                <input type="submit" />
            </form>
        </div>
    )
}
