import { wishlist } from "./state";
import { WISHLIST} from "./type";

export const wishReducer = (state=wishlist, action)=>{
    switch(action.type){
        case WISHLIST:
            // state.arr.push(action.value)
            let x = state.arr.find(e => e.user_id == action.value.user_id && e.product_id == action.value.product_id)
            if (x) {
                let prod = productsState.products.find(e => e.id == x.product_id);
                
            } else {
                state.arr.push({ ...action.value, id: Date.now(), quantitiy: 1 })
            }
            break;
        
        default:
            break
    }

    
    return {...state}
}
