import React from 'react'
import { useDispatch, useSelector } from "react-redux";
import { delCart, minusCart, plusCart } from "../../store/tocart/action";
// import "./ToCart.css"

const ToCart = () => {
    const state = useSelector((state)=>state.productData.products)
    const dispatch = useDispatch()
    const handleClose =(item)=>{
        dispatch(delCart(item))
    }
    const cartItems = (cartItem)=>{
            return(
                <div className='px-4 my-5 bg-light rounded-3'>
                    <div className='container py-4'>
                        <button onClick={()=>handleClose(cartItem)} className='btn'>X</button>
                        <div className='row justify-content-center'>
                            <div className='col-md-4'>
                                <img src={cartItem.img} height="200px" width="180px" />

                            </div>
                            <div className='col-md-4'>

                                <h3>{cartItem.name}</h3>
                                <p>${cartItem.price}</p>

                            </div>

                        </div>

                    </div>

                </div>
            )
    } 

    const emptyCart = () => {

        return 
    }

    return(
        <>
        {state.lenght === emptyCart()}
        {state.lenght !== 0 && state.map(cartItems)}
        {/* <h1>aaaaaa</h1> */}
        </>
    )
}

export default ToCart;

