import React, { Component } from "react";
import { Button } from "../Button";
import { MenuItems } from "./MenuItems";
import "./Navbar.css"
import {Link, Outlet} from 'react-router-dom'

class Navbar extends Component {
    state = { clicked: false }
    handleClick = () => {
        this.setState({clicked:!this.state.clicked})
    }


    render() {
        return (
            <>
            <header>
                {/* <div className="header_div"> */}

            <nav className="NavbarItems">
              <Link to="/"> <h1 className="navbar-logo">ADVENTURE</h1> </Link>
              {/* <Link to="/"><img src='./img/adventure-logo.png'></img> </Link> */}


                <div className="menu-icon" onClick={this.handleClick}>
                    <i className={this.state.clicked ? "fas fa-times": "fas fa-bars"}></i>

                </div>
                <ul className={this.state.clicked ? "nav-menu active" : "nav-menu"}>
                    {MenuItems.map((item,index) => {
                        return (
                            <li key={index}>
                                <Link className={item.cName} to={item.url}>
                                    {item.title}
                                </Link>
                            </li>

                        )
                    })}
                </ul>

                <div className="navbar_icon">
                <Button className='btn' onClick={()=>{

                }}> <i className="fa fa-user"></i></Button>
                <Button className='btn'> <i className="far fa-heart"></i></Button>
                <Button className='btn'> <i className='fas fa-shopping-bag'></i></Button>
                <Button className='btn'> <i className="fa fa-search"></i></Button>
                </div>




            </nav>
            {/* </div> */}
            </header>

            <Outlet/>
{/* 
            <footer>
                <h1>FOOTER</h1>
            </footer> */}
            </>
        )
    }
}

export default Navbar;
