import React, { Component } from "react";
import { Button } from "../Button";
import { MenuItems } from "./MenuItems";
import "./Navbar.css"
import {Link, Outlet} from 'react-router-dom'

class Navbar extends Component {
    state = { clicked: false }

    handleClick = () => {
        this.setState({clicked:!this.state.clicked})
    }


    render() {
        return (
            <>
            <header>
                <div className="header_div">

            <nav className="NavbarItems">
                <h1 className="navbar-logo">BLANSH</h1>
                <div className="menu-icon" onClick={this.handleClick}>
                    <i className={this.state.clicked ? "fas fa-times": "fas fa-bars"}></i>

                </div>
                <ul className={this.state.clicked ? "nav-menu active" : "nav-menu"}>
                    {MenuItems.map((item,index) => {
                        return (
                            <li key={index}>
                                <Link className={item.cName} to={item.url}>
                                    {item.title}
                                </Link>
                            </li>

                        )
                    })}
                </ul>

                <div >
                <Link to="/login"><Button className='btn'> <i class="fa fa-user"></i></Button></Link>
                <Button className='btn'> <i class="far fa-heart"></i></Button>
                <Button className='btn'> <i class='fas fa-shopping-bag'></i></Button>
                <Button className='btn'> <i class="fa fa-search"></i></Button>
                </div>




            </nav>
            </div>
            </header>

            <Outlet/>
{/* 
            <footer>
                <h1>FOOTER</h1>
            </footer> */}
            </>
        )
    }
}

export default Navbar;
