import { card } from "./state";
import { NEW_CARD } from "./type";

export const cardReducer = (state=card, action)=>{
    switch(action.type){
        
        case NEW_CARD:
            state.profile = action.value
            break;
        default:
            break
    }

    
    return {...state}
}
