import { useSelector } from "react-redux";
import { useState, useMemo } from "react";
import Product from "../products/Product";
import "./Home.css"

function Home() {
    const arr = ['Man', 'Woman', 'Kid', "Accessories"]
    const { products } = useSelector(state => state.productData)
    const filter = () => {
        let arr = []
        loop1: for (let i = 0; i < products.length; i++) {
            for (let j = 0; j < arr.length; j++) {
                if (arr[j]?.category === products[i].category) {
                    continue loop1
                }
            }
            arr.push(products[i])
        }
        return arr
    };
    const filterProducts = useMemo(() => filter(), [products]);
    console.log(filterProducts)

    return (
        <>
            <div className="header_div"></div>
            <div className="row">
                {
                    arr.map((e, i) => {
                        return (
                            <div className="col-sm-3mt-4 p-5 bg-primary text-white rounded">
                                <h1>Jumbotron Example</h1>
                                <p>Lorem ipsum...</p>
                            </div>
                        )
                    })
                }
            </div>
            <div className="image-grid">
                {
                    filterProducts.map((el, index) => {
                        return (
                            <Product product={el} key={el.id} />
                        )
                    })
                }
            </div>
        </>
    )
} 

export default Home;
