import { productsState } from "../products/state";
import { cart } from "./state";
import { NEW_CART } from "./type";

export const cartReducer = (state=cart, action)=>{
    switch(action.type){
        
        case NEW_CART:
            let x = state.arr.find(e=>e.user_id == action.value.user_id && e.product_id == action.value.product_id)
            if(x){
                let prod = productsState.products.find(e=>e.id == x.product_id);
                if(prod.count>x.quantitiy){
                    x.quantitiy++
                }
            }else{
                state.arr.push({...action.value, id:Date.now(), quantitiy:1})
            }
        break;
        default:
            break
    }

    
    return {...state}
}
