import { useSelector } from "react-redux";
import { useState, useMemo } from "react";
import Product from "../products/Product";
import "./Home.css"
import { Link } from "react-router-dom";

function Home() {
    // const arr = [{name:'Men', img:"/img/Men/1.png", link:"men"}, {name:'Women', img:"/img/Women/1.jpg", link:"women"}, {name:'Kid', img:"/img/Kids/1.jpg", link:"kid"}, {name:"Accessories", img:"/img/Accessories/1.jpg", link:"accessories"},{name: "Good", img:"/img/Accessories/1.jpg", link:"good"},{name: "hello", img:"/img/Accessories/1.jpg", link:"hello"},{name: "hello", img:"/img/Accessories/1.jpg" , link:"hello"}]
    const arr = useSelector(st=>st.catData.arr)

    const { products } = useSelector(state => state.productData)
   

    return (
        <>
            <div className="header_div"></div>
            {/* <div className="container"> */}

                <div className="outerDiv">
                    {
                        arr?.map((e) => {
                            return (
                                <div key={e.id} className="innerDiv" style={{background:"url(http://localhost:3000/"+e.img+")", backgroundRepeat: 'no-repeat',
                                backgroundSize: 'contain', backgroundPosition:"center"}}>
                                    <h1 style>{e.name}</h1>
                                    <Link to={{pathname:"/cat/"+e.link}}>See all</Link>
                                </div>
                            )
                        })
                    }
                </div>
            {/* </div> */}
            <div className="image-grid">
                {
                    products?.map((el, index) => {
                        return (
                            <Product product={el} key={el.id} />
                        )
                    })
                }
            </div>
        </>
    )
}

export default Home;
