import { useForm } from "react-hook-form"

import React from 'react'
import { useDispatch, useSelector } from "react-redux";
import { newProduct } from "../../store/products/action";

function MyProduct() {
    const product = useSelector(st=>st.productData)
    const user = useSelector(st=>st.userData.profile)
    const dispatch = useDispatch();
    const arr = ["Men", "Women", "Kid", "Accessories"]
    const arrImg = ["/img/09.png", "/img/10.png", "/img/08.png"]
    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm();
