import { useForm } from "react-hook-form"

import React from 'react'
import { useDispatch, useSelector } from "react-redux";
import { addNewUser } from "../../../store/user/action";

function MyProduct() {
    const users = useSelector(st=>st.productData)
    const dispatch = useDispatch();
    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm();
    const onSubmit = (data) => {
        console.log(data);
        const use = users.find(e=>e.email==data.email);
        if(use){
            alert('email is already');
        }else{
            if(data.password==data.confirm_password){
                dispatch(addNewUser({...data}));
            }else{
                alert('password invalid')
            }
        }

    };
    return (
        <>
            <h1>MyProduct</h1>
            <form onSubmit={handleSubmit(onSubmit)}>
                <h3>Registration</h3>
                <input placeholder='name'{...register('name', { required: true })} />
                {errors.name && <p>name is required.</p>}
                <input placeholder='surname'{...register('surname', { required: true })} />
                {errors.surname && <p>surname is required.</p>}
                <input placeholder='age' {...register('age', { pattern: /\d+/, required: true })} />
                {errors.age && <p>Please enter number for age.</p>}
                <input placeholder="email" {...register('email', { required: true, pattern: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/ })} />
                {errors.email && <p>email is required.</p>}
                <input placeholder='password' type='password' {...register('password', { required: true })} />
                {errors.password && <p>password is required.</p>}
                <input placeholder='confirm password' type='password' {...register('confirm_password', { required: true })} />
                {errors.confirm_password && <p>confirm password is required.</p>}
                <input className="inpSub" type="submit" />
            </form>
               </>
    )
}

export default MyProduct;
