import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import "./Product.css"



function Product({ product }) {
    console.log(product);
    const cart = useSelector(st=>st.tocartData);
    console.log("cart===>", cart);
    const dispatch = useDispatch()
    return (
        <div className="div_main" style={{border:'1px solid '}}>
            <div className="div_img">
                <img className="image-grid-col-2" src={product.img[0]} />
            </div>
            <div className="div_txt">
                <h1>{product.name}</h1>
                <h3>{product.category}</h3>
                <div ><p><i className="far fa-heart"></i> : <i className="fa fa-heart" style={{color:'red'}}></i></p></div>
                <div ><p><i className='fas fa-cart-arrow-down' onClick={()=>{
                    dispatch(add)
                }}></i></p></div>
            </div>
        </div>
    )
}

export default Product;
