import React, { Component, useState } from "react";
import { Button } from "../Button";
import { MenuItems } from "./MenuItems";
import "./Navbar.css"
import {Link, Outlet} from 'react-router-dom'

function Navbar() {
    const [state, setState ]= useState({clicked: false })

    const handleClick = () => {
        setState({clicked:!state.clicked})
    }


     {
        return (
            <>
            <header>

            <nav className="NavbarItems">
              <Link to="/"> <h1 className="navbar-logo">ADVENTURE</h1> </Link>


                <div className="menu-icon" onClick={handleClick}>
                    <i className={state.clicked ? "fas fa-times": "fas fa-bars"}></i>

                </div>
                <ul className={state.clicked ? "nav-menu active" : "nav-menu"}>
                    {MenuItems.map((item,index) => {
                        return (
                            <li key={index}>
                                <Link className={item.cName} to={item.url}>
                                    {item.title}
                                </Link>
                            </li>

                        )
                    })}
                </ul>

                <div className="navbar_icon">
                <Link to="/login"><Button className='btn'> <i className="fa fa-user"></i></Button></Link>
                <Link to="/profile"><Button className='btn'> <i className="fa fa-user"></i></Button></Link>
                <Button className='btn'> <i className="far fa-heart"></i></Button>
                <Button className='btn'> <i className='fas fa-shopping-bag'></i></Button>
                <Button className='btn'> <i className="fa fa-search"></i></Button>
                </div>




            </nav>
            </header>

            <Outlet/>

            </>
        )
    }
}

export default Navbar;
