import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from "react-redux";
import { useParams } from 'react-router-dom';
import { addCart, delCart, minusCart, plusCart } from "../../store/tocart/action";
// import "./ToCart.css"

const ToCart = () => {
    const state = useSelector((st) => st.tocartData.arr)
    const profile = useSelector((st) => st.userData.profile)
    const product = useSelector((st) => st.productData.products)
    const dispatch = useDispatch()
    const params = useParams()
    const [data, setData] = useState([])

    useEffect(() => {
        let arr = state.filter(e => e.user_id == profile.id);
        for (let e of arr) {
            e.prod = product.find(a => a.id == e.product_id)
        }
        console.log("arr===>", arr);
        setData([...arr])
    }, [state])

    console.log(state, profile);

   

    return (<div>
            <div class="container mt-3">
                <h2>Hoverable Dark Table</h2>
                <p>The .table-hover class adds a hover effect (grey background color) on table rows:</p>
                <table class="table table-dark table-hover">
                    <thead>
                        <tr>
                            <th>Product Name</th>
                            <th>Img</th>
                            <th>Price</th>
                            <th>count</th>
                            <th>quantity</th>
                            <th>total</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            data?.map((e) => {
                                return (
                                    <tr>
                                        <td>{e.prod.name}</td>
                                        <td><img src={'http://localhost:3000/'+e.prod.img[0]} width='100'/></td>
                                        <td>$ {e.prod.price}</td>
                                        <td>{e.prod.count}</td>
                                        <td>{e.quantity}</td>
                                        <td>$ {e.quantity*e.prod.price}</td>
                                        <td>
                                            <button onClick={()=>{
                                                dispatch(pl)
                                            }} className='btn btn-outline-warning text-white'>+</button>
                                            <button className='btn btn-outline-warning text-white'>-</button>
                                            <button className='btn btn-danger text-white'>&times;</button>
                                        </td>
                                    </tr>

                                )
                            })

                        }
                    </tbody>
                </table>
                </div>
            </div>
            )
}

            export default ToCart;
