import React from 'react'
import { useDispatch, useSelector } from "react-redux";
import { useParams } from 'react-router-dom';
import { delCart, minusCart, plusCart, addCart } from "../../store/tocart/action";
// import "./ToCart.css"

const ToCart = () => {
    const state = useSelector((state)=>state.productData.products)
    const dispatch = useDispatch()
    const params = useParams()

    const handleClose =(item)=>{
        dispatch(delCart(item))
    }
    const addCart = ()=>{
        dispatch()
    }
    const cartItems = (params)=>{
            return(
                <div className='px-4 my-5 bg-light rounded-3' key={params.id}>
                    <div className='container py-4'>
                        <button onClick={()=>handleClose(params)} className='btn'>X</button>
                        <div className='row justify-content-center'>
                            <div className='col-md-4'>
                                <img src={params.img} height="200px" width="180px" />

                            </div>
                            <div className='col-md-4'>

                                <h3>{params.name}</h3>
                                <p>${params.price}</p>

                            </div>

                        </div>

                    </div>

                </div>
            )
    } 

    const emptyCart = () => {

        return (
            <div className='px-4 my-5 bg-light rounded-3'>
                <div className='container py-4'>
                    <div className='row'>
                        <h3>Your Cart is Empty</h3>
                    </div>

                </div>
            </div>

        )
    }

    return(
        <>
        {state.lenght === emptyCart()}
        {state.lenght !== 0 && state.map(cartItems)}
        </>
    )
}

export default ToCart;
