import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from "react-redux";
import { useParams } from 'react-router-dom';
import { addCart, delCart, minusCart, plusCart } from "../../store/tocart/action";
// import "./ToCart.css"

const ToCart = () => {
    const state = useSelector((st) => st.tocartData.arr)
    const profile = useSelector((st) => st.userData.profile)
    const product = useSelector((st) => st.productData.products)
    const dispatch = useDispatch()
    const params = useParams()
    const [data, setData] = useState([])

    useEffect(() => {
        let arr = state.filter(e => e.user_id == profile.id);
        for (let e of arr) {
            e.prod = product.find(a => a.id == e.product_id)
        }
        console.log("arr===>", arr);
        setData([...arr])
    }, [state])

    console.log(state, profile);

    const handleClose = (item) => {
        dispatch(delCart(item))
    }
    // const addC = ()=>{
    //     dispatch(addCart)
    // }
    // const cartItems = (params)=>{
    //         return(
    //             <div className='px-4 my-5 bg-light rounded-3' key={params.id} >
    //                 <div className='container py-4'>
    //                     <button onClick={()=>handleClose(params)} className='btn'>X</button>
    //                     <div className='row justify-content-center'>
    //                         <div className='col-md-4'>
    //                             <img src={params.img} height="200px" width="180px" />

    //                         </div>
    //                         <div className='col-md-4'>

    //                             <h3>{params.user_id}</h3>
    //                             <p>${params.product_id}</p>
    //                             <p>{params.quantity}</p>


    //                         </div>

    //                     </div>

    //                 </div>

    //             </div>
    //         )
    // } 

    // const emptyCart = () => {

    //     return (
    //         <div className='px-4 my-5 bg-light rounded-3'>
    //             <div className='container py-4'>
    //                 <div className='row'>
    //                     <h3>Your Cart is Empty</h3>
    //                 </div>

    //             </div>
    //         </div>

    //     )
    // }

    return (<div>
            <div class="container mt-3">
                <h2>Hoverable Dark Table</h2>
                <p>The .table-hover class adds a hover effect (grey background color) on table rows:</p>
                <table class="table table-dark table-hover">
                    <thead>
                        <tr>
                            <th>Product Name</th>
                            <th>Img</th>
                            <th>Price</th>
                            <th>count</th>
                            <th>quantity</th>
                            <th>total</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            data?.map((e) => {
                                return (
                                    <tr>
                                        <td>{e.prod.name}</td>
                                        <td><img src={'http://localhost:3001/'+e.prod.img[0]} width='100'/></td>
                                        <td>john@example.com</td>
                                    </tr>

                                )
                            })

                        }
                    </tbody>
                </table>
                </div>
            </div>
            )
}

            export default ToCart;
