import { useSelector } from "react-redux";
import { useState, useMemo } from "react";
import Product from "../products/Product";
import "./Home.css"
import { Link } from "react-router-dom";

function Home() {
    const arr = [{name:'Men', img:"/img/Men/1.png", link:"men"}, {name:'Woman', img:"/img/Women/1.jpg", link:"women"}, {name:'Kid', img:"/img/Kids/1.jpg", link:"men"}, {name:"Accessories", img:"/img/Accessories/1.jpg"},{name: "Good", img:"/img/Accessories/1.jpg"},{name: "hello", img:"/img/Accessories/1.jpg"},{name: "hello", img:"/img/Accessories/1.jpg"}]
    const { products } = useSelector(state => state.productData)
    const filter = () => {
        let arr = []
        loop1: for (let i = 0; i < products.length; i++) {
            for (let j = 0; j < arr.length; j++) {
                if (arr[j]?.category === products[i].category) {
                    continue loop1
                }
            }
            arr.push(products[i])
        }
        return arr
    };
    const filterProducts = useMemo(() => filter(), [products]);
    console.log(filterProducts)

    return (
        <>
            <div className="header_div"></div>
            {/* <div className="container"> */}

                <div className="outerDiv">
                    {
                        arr.map((e, i) => {
                            return (
                                <div className="innerDiv" style={{background:"url(http://localhost:3000/"+e.img+")", backgroundRepeat: 'no-repeat',
                                backgroundSize: 'contain', backgroundPosition:"center"}}>
                                    <h1>{e.name}</h1>
                                    <Link to="">See all</Link>
                                </div>
                            )
                        })
                    }
                </div>
            {/* </div> */}
            <div className="image-grid">
                {
                    filterProducts.map((el, index) => {
                        return (
                            <Product product={el} key={el.id} />
                        )
                    })
                }
            </div>
        </>
    )
}

export default Home;
