import React from 'react'
import { useDispatch, useSelector } from "react-redux";
import { plusCart } from "../../store/tocart/action";
import "./ToCart.css"


function ToCart()  {
    const prod = useSelector(st=>st.tocartData.arr)
    
    const users = useSelector(st=>st.userData.profile)

    const dispatch = useDispatch();

    return(
        <>
        <h1>To Cart</h1>
        <table>
            <tbody>
                {prod.map((item, index)=>{
                    return(
                        <tr key={index}>
                        <td>
                            <img src={item.img}/>
                        </td>
                        <td>{item.user_id}</td>
                        <td>{item.product_id}</td>
                        <td>{item.quantity}</td>
                        <td>
                            <button>-</button>
                        </td>
                        <td>
                            <button onClick={()=>dispatch(plusCart(index))}>+</button></td>
                           
                            <button>Remove Item</button>
                        </tr>
                    )
                })
                }
            </tbody>
        </table>
        </>
    )
}

export default ToCart;
