import { useForm } from "react-hook-form"

import React from 'react'
import { useDispatch, useSelector } from "react-redux";

function MyProduct() {
    const users = useSelector(st=>st.productData)
    const dispatch = useDispatch();
    const arr = ["Men", "Women", "Kid", "Accessories"]
    const arrImg = ["/img/09.png", "/img/10.png", "/img/08.png"]
    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm();
    const onSubmit = (data) => {
        console.log({...data, id:Date.now()});
    };

    return (
        <>
            <h1>MyProduct</h1>
            <form onSubmit={handleSubmit(onSubmit)}>
                <h3>Registration</h3>
                <input placeholder='name'{...register('name', { required: true })} />
                {errors.name && <p>name is required.</p>}
                <input placeholder='count' {...register('count', { pattern: /\d+/, required: true })} />
                {errors.count && <p>Please enter number for count.</p>}
                <input placeholder='price' {...register('price', { pattern: /\d+/, required: true })} />
                {errors.price && <p>Please enter number for price.</p>}
                <input placeholder='description'{...register('description', { required: true })} />
                {errors.description && <p>description is required.</p>}
               <select {...register('category', { required: true })}>
                   {arr.map((e, i)=>{
                       return <option key={i} value={e}>{e}</option>
                   })}
               </select>
               <select multiple {...register('img', { required: true })}>
                   {arrImg.map((e, i)=>{
                       return <option key={i} value={e}>{e}</option>
                   })}
               </select>
                <input className="inpSub" type="submit" />
            </form>
               </>
    )
}

export default MyProduct;
