import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { useDispatch } from "react-redux";
import { productsState } from "../../store/products/state";
import { addItem, delItem } from "../../store/products/action";

const ProductDetail = () =>{
    const [cartBtn, setCartBtn] = useState()
    const proid = useParams();
    const proDetail = productsState.filter(x=>x.id == proid.id)
    const product = proDetail[0]
    const dispatch = useDispatch()
    console.log(product);
    const handleCart = (product) =>{
        if(cartBtn === "Add to Cart"){
            dispatch(addItem(product))
            setCartBtn("Remove from Cart")
        }else{
            dispatch(delItem(product))
            setCartBtn("Add to Cart")
        }

    }

    return(
        <>
        <div className="container my-5 py-3">
            <div className="row">
                <div className="col-md-6 d-flex justify-content-center mx-auto product">
                    <img src={product.img} alt={product.name} height="400px"/>
                </div>
                <div className="col-md-6 d-flex flex-column justify-content-center">
                    <h1 className="display-5 fw-bold">{product.name}</h1>
                    <hr />
                    <h2 className="my-4">${product.price}</h2>
                    <p className="lead">{product.description}</p>
                    <button onClick={()=>handleCart(product)} className="btn btn-outline-primary my-4">{cartBtn}</button>
                </div>
            </div>
        </div>

        </>
    )
}

export default ProductDetail;
