import React, { Component } from "react";
import { Button } from "../Button";
import { MenuItems } from "./MenuItems";
import "./Navbar.css"

class Navbar extends Component {
    state = { clicked: false }

    handleClick = () => {
        this.setState({clicked:!this.state.clicked})
    }


    render() {
        return (
            <header>

            <nav className="NavbarItems">
                <h1 className="navbar-logo">BLANSH</h1>
                <img className='imgLogo' src='https://i.pinimg.com/originals/0f/e7/51/0fe751a14ab3ce2a18d46db030f7bf1d.png'></img>
                <div className="menu-icon" onClick={this.handleClick}>
                    <i className={this.state.clicked ? "fas fa-times": "fas fa-bars"}></i>

                </div>
                <ul className={this.state.clicked ? "nav-menu active" : "nav-menu"}>
                    {MenuItems.map((item,index) => {
                        return (
                            <li key={index}>
                                <a className={item.cName} href={item.url}>
                                    {item.title}
                                </a>
                            </li>

                        )
                    })}
                </ul>


                <Button className='btn'> <i class="fa fa-user"></i></Button>
                <Button className='btn'> <i class="far fa-heart"></i></Button>
                <Button className='btn'> <i class='fas fa-shopping-bag'></i></Button>
                <Button className='btn'> <i class="fa fa-search"></i></Button>




                 {/* <div>
                    <span><i class="fa-solid fa-magnifying-glass"></i></span>
                    <input type="text" placeholder="Search" />

                </div> */}

            </nav>
            </header>
        )
    }
}

export default Navbar
