import {NEW_PRODUCT} from './type'
import { DEL_PRODUCT } from './type'


export const newProduct=(value)=>{
    return {type: NEW_PRODUCT, value: value}
}

export const delProduct=(value)=>{
    return {type: DEL_PRODUCT, value: value}
}
