import { productsState } from "./state";
import { DEL_PRODUCT, NEW_PRODUCT } from "./type";

export const productReducer = (state=productsState, action)=>{
    switch(action.type){
        case NEW_PRODUCT:
            state.products.push(action.value)
            break;
        case DEL_PRODUCT:
            state.products.splice(action.value)
            break;
        default:
            break
    }

    
    return {...state}
}
